package java01;

public class SumAve1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int x, y;
	double z;
	double sum;
	
	x = 63;
	y = 18;
	z = 350.888;
	sum = x + y + z;
	
	System.out.println("x값 : " + x + "이다");
	System.out.println("x값 + y값 : " + (x+y) +"이다");
	System.out.println(sum);
	System.out.println(x+y+z);
	}

}
